    private void connectActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_connectActionPerformed
        try {
            // Ao clicar no botão conectar o Socket é criado com as informações concebidas
            // E logo em seguida, o lobby também é criado.
            Socket client = new Socket(txtIP.getText(), Integer.parseInt(txtPort.getText()));
            
            new Lobby(client).setVisible(true);
            this.dispose();
        } catch (java.net.ConnectException ex) {
            JOptionPane.showMessageDialog(null, "Não há servidor nesta porta.");
        } catch (java.net.UnknownHostException | java.net.NoRouteToHostException ex) {
            JOptionPane.showMessageDialog(null, "IP inválido!");
        } catch (java.lang.NumberFormatException ex) {
            JOptionPane.showMessageDialog(null, "A porta é dada em números inteiros!");
        } catch (IllegalArgumentException ex) {
            JOptionPane.showMessageDialog(null, "Porta inválida! Tente outra.");
        } catch (java.io.IOException ex) {
            System.out.println("Error: " + ex);
        } 
    }//GEN-LAST:event_connectActionPerformed

